





    


SELECT 
    UUID(),
    CONCAT(fn.first_name, ' ', ln.last_name),
    CONCAT(fn.first_name, ln.last_name, SUBSTRING(0, 5)),
    SUBSTRING(MD5(RAND()) FROM 1 FOR 10)
FROM 
    global_first_names fn
    CROSS JOIN global_last_names ln
ORDER BY 
    RAND()
LIMIT 100;

CALL InsertMultipleTopics(100);
CALL InsertMultipleIdeas(100);

SELECT 
    UUID(), 
    IF(RAND() < 0.5, 1, 0) AS positive, -- Genera aleatoriamente 1 (positivo) o 0 (negativo)
    (SELECT id FROM users ORDER BY RAND() LIMIT 1) AS userId, -- ID de usuario aleatorio
    (SELECT id FROM ideas ORDER BY RAND() LIMIT 1) AS ideaId -- ID de idea aleatorio
FROM 
    -- La tabla "dummy" se utiliza para generar m�ltiples filas
    (SELECT 1 AS dummy FROM ideas LIMIT 100) AS d;
select * from votes;
select * from users;
select * from topics;

select * from ideas;

DROP PROCEDURE IF EXISTS InsertMultipleTopics;
DROP PROCEDURE IF EXISTS InsertMultipleIdeas;